  return (
		<div className="note" style={{
      backgroundColor: color,
      height: "300px",
      transition: "ease all 500ms"
    }}>
			<span>{text}</span>
			<div className='note-footer'>
				<small>{date}</small>
        <MdModeEdit />
				<MdDeleteForever
					onClick={() => deleteNote(id)}
					className='delete-icon'
					size='1.3em'
				/>
			</div>
		</div>
	);
};

export default Note;
